mapper.select:
        
          
             and qav.version_id = #{versionId}
              and qav.project_id = #{projectId}
              and qav.version_number like concat('%', #{versionNumber}, '%')
              and qav.version_status = #{versionStatus}
              and qav.leader = #{leader}
              and qav.developer = #{developer}
              and qav.tester = #{tester}
              and qav.plan_finish_date = #{planFinishDate}
              and qav.actually_finish_date = #{actuallyFinishDate}
             
			    and date_format(qav.launch_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
		     
		     
			    and date_format(qav.launch_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
		     
              and qav.time_limit_version = #{timeLimitVersion}
              and qav.project_deviation_days = #{projectDeviationDays}
              and qav.project_deviation_percent = #{projectDeviationPercent}
              and qav.demand_plan_finish = #{demandPlanFinish}
              and qav.demand_actually_finish = #{demandActuallyFinish}
              and qav.demand_percent = #{demandPercent}
              and qav.testcase_count = #{testcaseCount}
              and qav.testing_return = #{testingReturn}
              and qav.dev_human_resources = #{devHumanResources}
              and qav.test_human_resources = #{testHumanResources}
              and qav.change_line_code = #{changeLineCode}
              and qav.bug_zm = #{bugZm}
              and qav.bug_yz = #{bugYz}
              and qav.bug_yb = #{bugYb}
              and qav.bug_ts = #{bugTs}
              and qav.code_di = #{codeDi}
              and qav.quality_review = #{qualityReview}
              and qav.imprint = #{imprint}
              and qav.remark = #{remark}
              and qaa.create_by = #{createBy}
              and qaa.create_time = #{createTime}
              and qaa.update_by = #{updateBy}
              and qaa.update_time = #{updateTime}
         
         order by IF(ISNULL(qav.launch_date),0,1) asc,qav.actually_finish_date desc,qav.version_id desc

mapper.update:
update qa_version
project_id = #{projectId}, version_number = #{versionNumber}, version_status = #{versionStatus}, leader = #{leader}, developer = #{developer}, tester = #{tester}, plan_finish_date = #{planFinishDate}, actually_finish_date = #{actuallyFinishDate}, launch_date = #{launchDate}, time_limit_version = #{timeLimitVersion}, project_deviation_days = #{projectDeviationDays}, project_deviation_percent = #{projectDeviationPercent}, demand_plan_finish = #{demandPlanFinish}, demand_actually_finish = #{demandActuallyFinish}, demand_percent = #{demandPercent}, testcase_count = #{testcaseCount}, testing_return = #{testingReturn}, dev_human_resources = #{devHumanResources}, test_human_resources = #{testHumanResources}, change_line_code = #{changeLineCode}, bug_zm = #{bugZm}, bug_yz = #{bugYz}, bug_yb = #{bugYb}, bug_ts = #{bugTs}, code_di = #{codeDi}, quality_review = #{qualityReview}, imprint = #{imprint}, remark = #{remark}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where version_id = #{versionId}
