mapper.select:


    and pc.case_id = #{caseId}
     and pc.case_serial_number = #{caseSerialNumber}
     and pc.case_sign like concat('%', #{caseSign}, '%')
     and pc.case_name like concat('%', #{caseName}, '%')
     and pc.project_id = #{projectId}
     and (pc.module_id = #{moduleId} or pc.module_id in (select module_id from project_case_module where ancestors like concat('%,', #{moduleId}, ',%') or ancestors like concat('%,', #{moduleId}, '')))
     and pc.case_type = #{caseType}
     and pc.failcontinue = #{failcontinue}
     and pc.create_by = #{createBy}
     and pc.create_time = #{createTime}
     and pc.update_by = #{updateBy}
     and pc.update_time = #{updateTime}
     and pc.remark  like concat('%', #{remark}, '%')

order by pc.case_id desc

select pc.case_id, pc.case_serial_number, pc.case_sign, pc.case_name, pc.project_id, pc.module_id, pc.case_type, pc.failcontinue, pc.create_by, pc.create_time, pc.update_by, pc.update_time, pc.remark,
pcm.module_name,
ppc.plan_case_id, ppc.plan_id, ppc.priority  
from project_case pc 
left join project_case_module pcm on pc.module_id = pcm.module_id 
left join project_plan_case ppc on pc.case_id = ppc.case_id

     and pc.case_id = #{caseId}
     and pc.case_serial_number = #{caseSerialNumber}
     and pc.case_sign like concat('%', #{caseSign}, '%')
     and pc.case_name like concat('%', #{caseName}, '%')
     and pc.project_id = #{projectId}
     and pc.module_id = #{moduleId} or pc.module_id in (select module_id from project_case_module where ancestors like concat('%,', #{moduleId}, ',%') or ancestors like concat('%,', #{moduleId}, ''))
     and pc.case_type = #{caseType}
     and pc.failcontinue = #{failcontinue}
     and pc.create_by = #{createBy}
     and pc.create_time = #{createTime}
     and pc.update_by = #{updateBy}
     and pc.update_time = #{updateTime}
     and pc.remark  like concat('%', #{remark}, '%')
     and ppc.plan_id = #{planId}

 order by ppc.priority asc,pc.case_id desc

mapper.update:
update project_case
case_serial_number = #{caseSerialNumber}, case_sign = #{caseSign}, case_name = #{caseName}, project_id = #{projectId}, module_id = #{moduleId}, case_type = #{caseType}, failcontinue = #{failcontinue}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where case_id = #{caseId}
